package day1;

public class DataTypeDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*
		 * String - It will accept the complete sentence int - whole numbers float -
		 * decimal numbers char - 'A' 'AS' '$' ')' boolean - true/false Java is a case
		 * sensitive lang
		 */

		String str = "Hello Welcome to Java";
		int num = 123;
		float fl = 234.89f; // Literals
		char ch = 'A';
		boolean flag = true;

		System.out.println("String ->  " + str);
		System.out.println("Integer ->  " + num);
		System.out.println("Float ->  " + fl);
		System.out.println("Character ->  " + ch);
		System.out.println("Boolean ->  " + flag);
		
		/*
		 * System - final class - java.lang package out - Inside System class println -
		 * PrintStream class
		 */

	}

}
