package day5;

public class PostAndPre {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/*
		 * 
		 * i++
		 * i--
		 * ++i
		 * --i
		 * 
		 */
		
		int i = 10;
		System.out.println("The Value of i = "+ (i++));

		System.out.println("The Value of i = "+ i);
		
		System.out.println("The Value of i = "+ i--);

		System.out.println("The Value of i = "+ i);
		
		int j = 20;
		
		System.out.println("The Value of i = "+ (++j));

		System.out.println("The Value of i = "+ j);
		
		System.out.println("The Value of i = "+ (--j));

		System.out.println("The Value of i = "+ j);

	}

}
